edits.text:
{
  "layer": "top",
  "position": "top",
  "height": 28,
  "modules-left": [
    "custom/power",
    "custom/wallpaper",
    "window"
  ],
  "modules-center": [
    "wlr/taskbar"
  ],
  "modules-right": [
    "pulseaudio",
    "memory",
    "cpu",
    "custom/gpu",
    "custom/calendar",
    "clock"
  ],
  "custom/power": {
    "format": "",
    "on-click": "wofi-powermenu"
  },
  "custom/wallpaper": {
    "format": "",
    "on-click": "wofi-wallpaper"
  },
  "window": {
    "max-length": 40
  },
  "wlr/taskbar": {
    "icon-size": 18,
    "on-click": "activate"
  },
  "pulseaudio": {
    "format": " {volume}%"
  },
  "memory": {
    "format": " {used}G/{total}G"
  },
  "cpu": {
    "format": " {usage}%"
  },
  "custom/gpu": {
    "format": " N/A",
    "tooltip": false,
    "exec": "echo N/A", // Replace with a GPU usage script if you want
    "interval": 10
  },
  "custom/calendar": {
    "format": " {output}",
    "exec": "date +'%d/%m/%Y'",
    "on-click": "gsimplecal", // Change to your preferred calendar app
    "interval": 60
  },
  "clock": {
    "format": " {:%H:%M:%S}",
    "interval": 1
  }
}
