#cart-items::-webkit-scrollbar-thumb, .dialog::-webkit-scrollbar-thumb { background:#202a36; border-radius:999px; }
#cart-items::-webkit-scrollbar-thumb:hover, .dialog::-webkit-scrollbar-thumb:hover { background:#2d3a4c; }

@media (max-width:600px){
  header { flex-wrap:wrap; }
  .payment-methods button { flex:1 1 46%; }
}
