<html lang="en">
<head>
  <meta charset="UTF-8" />
  <meta name="viewport" content="width=device-width,initial-scale=1" />
  <title>Shop</title>
  <link rel="stylesheet" href="style.css" />
  <meta name="color-scheme" content="dark light" />
</head>
<body>
  <header>
    <h1>My WebApp Shop</h1>
    <div class="search-bar">
      <input id="search" type="text" placeholder="Search products..." autocomplete="off" />
    </div>
    <div class="category-filter" id="categoryFilter"></div>
    <button id="cart-btn" aria-haspopup="dialog" aria-controls="cart-drawer">
      <span>Cart</span>
      <span id="cart-count" aria-live="polite">0</span>
    </button>
  </header>

  <main class="wrapper">
    <section>
      <h2 style="margin-top:0">Products</h2>
      <div id="productGrid" class="grid products" aria-live="polite"></div>
    </section>
  </main>

  <!-- Cart Drawer -->
  <aside id="cart-drawer" role="dialog" aria-modal="true" aria-labelledby="cartTitle">
    <header style="display:flex;align-items:center;justify-content:space-between;gap:1rem;">
      <h2 id="cartTitle" style="margin:0;font-size:1.1rem;">Your Cart</h2>
      <button id="closeCart" class="remove-btn" aria-label="Close cart"></button>
    </header>
    <div id="cart-items"></div>
    <div class="cart-footer">
      <div class="total-line"><span>Subtotal</span><span id="cartSubtotal">$0.00</span></div>
      <button id="checkoutBtn" class="checkout-btn">Checkout</button>
      <small style="color:#6b7280;">No automatic payment capture. Manual confirmation after you pay via selected method.</small>
    </div>
  </aside>

  <!-- Checkout Modal -->
  <div class="dialog-backdrop" id="checkoutModal" aria-hidden="true">
    <div class="dialog" role="dialog" aria-modal="true" aria-labelledby="checkoutTitle">
      <button class="close-modal" id="closeCheckout" aria-label="Close checkout"></button>
      <h2 id="checkoutTitle">Checkout</h2>
      <div class="notice warn">This flow uses manual payment methods (DM / ticket / crypto / PayPal). After sending your payment, we will activate the access key you submit.</div>

      <section>
        <h3 style="margin:.25rem 0 .5rem;font-size:.9rem;letter-spacing:1px;text-transform:uppercase;opacity:.85;">1. Choose Payment Method</h3>
        <div class="payment-methods" id="paymentMethods">
          <button data-method="instagram" type="button">Instagram Ticket</button>
          <button data-method="discord" type="button">Discord Ticket</button>
          <button data-method="telegram" type="button">Telegram DM</button>
          <button data-method="crypto" type="button">Crypto</button>
          <button data-method="paypal" type="button">PayPal</button>
        </div>
      </section>

      <form id="checkoutForm" novalidate>
        <h3 style="margin:.75rem 0 .5rem;font-size:.9rem;letter-spacing:1px;text-transform:uppercase;opacity:.85;">2. Contact & Access Key</h3>
        <div style="display:grid;gap:1rem;">
          <div>
            <label for="contactHandle">Contact (Instagram @ / Discord tag / Telegram @ / Email)</label>
            <input id="contactHandle" name="contact" type="text" required placeholder="e.g. @yourhandle or user#1234" />
            <small class="help">We will reach out here if clarification needed.</small>
          </div>
          <div>
            <label for="accessKey">Desired Access Key</label>
            <input id="accessKey" name="accessKey" type="text" required minlength="4" maxlength="40" placeholder="Choose a key you'll use to login" />
            <small class="help">Will be tied to your purchase once payment confirmed.</small>
          </div>
        </div>

        <h3 style="margin:1rem 0 .5rem;font-size:.9rem;letter-spacing:1px;text-transform:uppercase;opacity:.85;">3. Instructions</h3>
        <div class="instructions" id="methodInstructions">
          <p>Select a payment method above to view specific instructions.</p>
        </div>

        <h3 style="margin:1rem 0 .5rem;font-size:.9rem;letter-spacing:1px;text-transform:uppercase;opacity:.85;">4. Submit Order Request</h3>
        <div class="notice" id="pendingSummary">Fill details to generate summary.</div>
        <button id="submitOrder" type="submit" class="checkout-btn" disabled>Send Order to Webhook</button>
        <small class="help">This sends your cart, contact, access key & chosen payment method to our processing webhook.</small>
      </form>
    </div>
  </div>

  <footer>
    <p>Placeholder shop interface. Replace webhook URL & crypto amount before production.</p>
  </footer>

  <script>
  // ----- Config -----
  const WEBHOOK_URL = 'https://example.com/your-webhook'; // TODO: replace with real webhook endpoint
  const CRYPTO_ADDRESS = '0xYOURCRYPTOADDRESSPLACEHOLDER';
  const CRYPTO_AMOUNT_PLACEHOLDER = '0.0123'; // Replace with required amount
  const PAYPAL_PLACEHOLDER = 'your-paypal-email@example.com';
  const TELEGRAM_USERNAME = '@YourTelegram';
  const DISCORD_SERVER = 'discord.gg/yourinvite';
  const INSTAGRAM_PROFILE = '@your_instagram';

  // Example product data
  const products = [
    { id: 'p1', name: 'Starter Plan', price: 10, category: 'plans', badge:'Popular', desc:'Basic access tier with core features.' },
    { id: 'p2', name: 'Pro Plan', price: 25, category: 'plans', badge:'Hot', desc:'Extended limits & premium support.' },
    { id: 'p3', name: 'Enterprise Plan', price: 60, category: 'plans', badge:'Value', desc:'High limits + priority integration help.' },
    { id: 'p4', name: 'Add-on: Extra Seats', price: 15, category: 'addons', desc:'Increase concurrent usage seats.' },
    { id: 'p5', name: 'Add-on: Priority Queue', price: 12, category: 'addons', desc:'Jump the processing queue for faster results.' },
    { id: 'p6', name: 'Support Pack', price: 20, category: 'support', desc:'Dedicated chat support pack.' }
  ];

  // ----- State -----
  const state = {
    cart: [], // {id, name, price, qty}
    method: null
  };

  // ----- Elements -----
  const productGrid = document.getElementById('productGrid');
  const cartBtn = document.getElementById('cart-btn');
  const cartDrawer = document.getElementById('cart-drawer');
  const closeCartBtn = document.getElementById('closeCart');
  const cartItemsEl = document.getElementById('cart-items');
  const cartSubtotalEl = document.getElementById('cartSubtotal');
  const cartCountEl = document.getElementById('cart-count');
  const checkoutBtn = document.getElementById('checkoutBtn');
  const checkoutModal = document.getElementById('checkoutModal');
  const closeCheckoutBtn = document.getElementById('closeCheckout');
  const paymentMethodsEl = document.getElementById('paymentMethods');
  const methodInstructions = document.getElementById('methodInstructions');
  const checkoutForm = document.getElementById('checkoutForm');
  const contactHandle = document.getElementById('contactHandle');
  const accessKey = document.getElementById('accessKey');
  const pendingSummary = document.getElementById('pendingSummary');
  const submitOrderBtn = document.getElementById('submitOrder');
  const searchInput = document.getElementById('search');
  const categoryFilter = document.getElementById('categoryFilter');

  // ----- Render Products -----
  function renderProducts(list){
    productGrid.innerHTML = '';
    list.forEach(p => {
      const card = document.createElement('div');
      card.className = 'product';
      card.innerHTML = `
        ${p.badge ? `<span class="badge">${p.badge}</span>`: ''}
        <h3>${p.name}</h3>
        <div style="font-size:.75rem;color:#9ca3af;line-height:1.3;">${p.desc||''}</div>
        <div class="price">$${p.price.toFixed(2)}</div>
        <button data-add="${p.id}">Add to Cart</button>
      `;
      productGrid.appendChild(card);
    });
  }

  // ----- Categories -----
  const categories = [...new Set(products.map(p=>p.category))];
  function renderCategories(){
    categoryFilter.innerHTML = '<button data-cat="all" class="active" type="button">All</button>' + categories.map(c=>`<button data-cat="${c}" type="button">${c}</button>`).join('');
  }
  renderCategories();

  categoryFilter.addEventListener('click', e => {
    if(e.target.matches('button[data-cat]')){
      const cat = e.target.getAttribute('data-cat');
      categoryFilter.querySelectorAll('button').forEach(b=>b.classList.remove('active'));
      e.target.classList.add('active');
      filterProducts();
    }
  });

  // ----- Filtering -----
  function filterProducts(){
    const term = searchInput.value.trim().toLowerCase();
    const activeCatBtn = categoryFilter.querySelector('button.active');
    const cat = activeCatBtn ? activeCatBtn.getAttribute('data-cat') : 'all';
    const filtered = products.filter(p => {
      const matchCat = cat === 'all' || p.category === cat;
      const matchTerm = !term || p.name.toLowerCase().includes(term) || (p.desc||'').toLowerCase().includes(term);
      return matchCat && matchTerm;
    });
    renderProducts(filtered);
  }
  searchInput.addEventListener('input', filterProducts);

  // Initial render
  renderProducts(products);

  // Add to cart handler via delegation
  productGrid.addEventListener('click', e => {
    const btn = e.target.closest('button[data-add]');
    if(!btn) return;
    const id = btn.getAttribute('data-add');
    const prod = products.find(p=>p.id===id);
    if(!prod) return;
    const existing = state.cart.find(i=>i.id===id);
    if(existing) existing.qty += 1; else state.cart.push({ id: prod.id, name: prod.name, price: prod.price, qty:1 });
    updateCartUI();
    openCart();
  });

  function updateCartUI(){
    cartItemsEl.innerHTML = '';
    if(state.cart.length === 0){
      cartItemsEl.innerHTML = '<div style="opacity:.6;font-size:.85rem;">Cart empty.</div>';
    } else {
      state.cart.forEach(item => {
        const div = document.createElement('div');
        div.className = 'cart-item';
        div.innerHTML = `
          <div style="display:flex;flex-direction:column;gap:2px;">
            <h4>${item.name}</h4>
            <small>$${item.price.toFixed(2)} each</small>
          </div>
          <div class="qty-controls">
            <button data-dec="${item.id}" aria-label="Decrease quantity">-</button>
            <span>${item.qty}</span>
            <button data-inc="${item.id}" aria-label="Increase quantity">+</button>
            <button class="remove-btn" data-remove="${item.id}" aria-label="Remove item"></button>
          </div>
        `;
        cartItemsEl.appendChild(div);
      });
    }
    const subtotal = state.cart.reduce((s,i)=> s + i.price * i.qty, 0);
    cartSubtotalEl.textContent = '$' + subtotal.toFixed(2);
    cartCountEl.textContent = state.cart.reduce((s,i)=> s + i.qty, 0);
    generateSummary();
  }

  cartItemsEl.addEventListener('click', e => {
    const dec = e.target.closest('button[data-dec]');
    const inc = e.target.closest('button[data-inc]');
    const rem = e.target.closest('button[data-remove]');
    if(dec){
      const id = dec.getAttribute('data-dec');
      const item = state.cart.find(i=>i.id===id);
      if(item){ item.qty = Math.max(1, item.qty-1); }
    }
    if(inc){
      const id = inc.getAttribute('data-inc');
      const item = state.cart.find(i=>i.id===id);
      if(item){ item.qty += 1; }
    }
    if(rem){
      const id = rem.getAttribute('data-remove');
      state.cart = state.cart.filter(i=>i.id!==id);
    }
    updateCartUI();
  });

  function openCart(){ cartDrawer.classList.add('open'); }
  function closeCart(){ cartDrawer.classList.remove('open'); }
  cartBtn.addEventListener('click', openCart);
  closeCartBtn.addEventListener('click', closeCart);
  document.addEventListener('keydown', e => { if(e.key==='Escape'){ closeCart(); closeCheckout(); } });

  // Checkout
  checkoutBtn.addEventListener('click', () => {
    if(state.cart.length === 0){ alert('Cart empty'); return; }
    checkoutModal.classList.add('open');
    checkoutModal.setAttribute('aria-hidden','false');
  });
  function closeCheckout(){ checkoutModal.classList.remove('open'); checkoutModal.setAttribute('aria-hidden','true'); }
  closeCheckoutBtn.addEventListener('click', closeCheckout);
  checkoutModal.addEventListener('click', e => { if(e.target === checkoutModal) closeCheckout(); });

  paymentMethodsEl.addEventListener('click', e => {
    const btn = e.target.closest('button[data-method]');
    if(!btn) return;
    paymentMethodsEl.querySelectorAll('button').forEach(b=>b.classList.remove('active'));
    btn.classList.add('active');
    state.method = btn.getAttribute('data-method');
    renderMethodInstructions();
    generateSummary();
  });

  function renderMethodInstructions(){
    let html = '';
    switch(state.method){
      case 'instagram':
        html = `<p>Open Instagram and send a message to <strong>${INSTAGRAM_PROFILE}</strong> with your access key <code>${escapeHtml(accessKey.value||'[YOUR_KEY]')}</code> and screenshot of payment if already sent.</p>`;
        break;
      case 'discord':
        html = `<p>Create a ticket in our Discord <strong>${DISCORD_SERVER}</strong> referencing access key <code>${escapeHtml(accessKey.value||'[YOUR_KEY]')}</code>. Provide payment proof after you pay.</p>`;
        break;
      case 'telegram':
        html = `<p>DM <strong>${TELEGRAM_USERNAME}</strong> on Telegram referencing access key <code>${escapeHtml(accessKey.value||'[YOUR_KEY]')}</code>. Send payment proof after paying.</p>`;
        break;
      case 'crypto':
        html = `<p>Send <strong>${CRYPTO_AMOUNT_PLACEHOLDER} (placeholder)</strong> in your chosen crypto to address:<br><code>${CRYPTO_ADDRESS}</code><br>Name or memo (if available) should include your access key <code>${escapeHtml(accessKey.value||'YOUR_KEY')}</code>. After sending, submit transaction hash via contact channel.</p>`;
        break;
      case 'paypal':
        html = `<p>Send the total amount to PayPal: <strong>${PAYPAL_PLACEHOLDER}</strong>. In the payment note put your access key <code>${escapeHtml(accessKey.value||'YOUR_KEY')}</code>. Then submit this form.</p>`;
        break;
      default:
        html = '<p>Select a payment method to see instructions.</p>';
    }
    methodInstructions.innerHTML = html;
  }

  function escapeHtml(str){ return str.replace(/[&<>"']/g, c => ({'&':'&amp;','<':'&lt;','>':'&gt;','"':'&quot;','\'':'&#39;'}[c])); }

  function generateSummary(){
    if(!state.method || state.cart.length===0 || !contactHandle.value.trim() || !accessKey.value.trim()){
      submitOrderBtn.disabled = true;
      pendingSummary.className = 'notice warn';
      pendingSummary.textContent = 'Fill cart, pick payment method, and enter contact + access key.';
      return;
    }
    const total = state.cart.reduce((s,i)=> s + i.price * i.qty, 0).toFixed(2);
    const lines = state.cart.map(i=> `${i.qty} x ${i.name} ($${(i.price*i.qty).toFixed(2)})`).join('; ');
    pendingSummary.className = 'notice ok';
    pendingSummary.innerHTML = `<strong>Summary:</strong> ${escapeHtml(lines)} | Total: $${total} | Method: ${escapeHtml(state.method)} | Contact: ${escapeHtml(contactHandle.value.trim())} | Key: <code>${escapeHtml(accessKey.value.trim())}</code>`;
    submitOrderBtn.disabled = false;
  }

  ['input','change'].forEach(ev => {
    contactHandle.addEventListener(ev, generateSummary);
    accessKey.addEventListener(ev, () => { renderMethodInstructions(); generateSummary(); });
  });

  checkoutForm.addEventListener('submit', async e => {
    e.preventDefault();
    generateSummary();
    if(submitOrderBtn.disabled) return;
    submitOrderBtn.disabled = true;
    submitOrderBtn.textContent = 'Sending...';

    const payload = {
      paymentMethod: state.method,
      contact: contactHandle.value.trim(),
      accessKey: accessKey.value.trim(),
      cart: state.cart.map(i=>({ id:i.id, name:i.name, qty:i.qty, unitPrice:i.price, lineTotal: +(i.price*i.qty).toFixed(2) })),
      total: state.cart.reduce((s,i)=> s + i.price * i.qty, 0),
      timestamp: new Date().toISOString(),
      userAgent: navigator.userAgent
    };

    try {
      const res = await fetch(WEBHOOK_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if(!res.ok) throw new Error('Webhook error ' + res.status);
      submitOrderBtn.textContent = 'Sent!';
      pendingSummary.className = 'notice ok';
      pendingSummary.innerHTML += '<br><br>Order request sent. Follow the instructions above to complete payment if not already.';
      // Optionally clear cart
      // state.cart = []; updateCartUI();
    } catch(err){
      console.error(err);
      submitOrderBtn.disabled = false;
      submitOrderBtn.textContent = 'Send Order to Webhook';
      pendingSummary.className = 'notice danger';
      pendingSummary.textContent = 'Failed to send to webhook: ' + err.message;
    }
  });

  </script>
</body>
</html>"}

